import React, { useState, useEffect } from "react";
import '/styles/Home.css'; // Assuming we will use external CSS for styling

const images = [
    "/public/art1.jpg", // Replace with your image paths
    "/public/art2.jpg",
    "/public/art3.jpg",
    "/public/art4.jpg",
    "/public/art5.jpg",
    "/public/art6.jpg",
];

function Home() {
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
        }, 2000); // Change image every 2 seconds

        return () => clearInterval(interval); // Cleanup interval on unmount
    }, []);

    return (
        <div className="home-container">
            <img src={images[currentImageIndex]} alt={`Slide ${currentImageIndex}`} className="image" />
        </div>
    );
}

export default Home;
